import React, { useState } from "react";
import { Form, Button, Container, Row, Col, InputGroup } from "react-bootstrap";
import { CloudUpload, Save, ArrowLeft } from "react-bootstrap-icons";

const RegistrationForm = () => {
  const [employeeCode, setEmployeeCode] = useState("");
  const [fullname, setFullname] = useState("");
  const [mobile, setMobile] = useState("");
  const [email, setEmail] = useState("");
  const [dob, setDob] = useState("");
  const [image, setImage] = useState(null);

  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform form validation and submission logic here
  };

  const handleDobChange = (e) => {
    const inputDate = new Date(e.target.value);
    const today = new Date();
    const age = today.getFullYear() - inputDate.getFullYear();
    if (age < 18 || (age === 18 && today < new Date(today.getFullYear(), inputDate.getMonth(), inputDate.getDate()))) {
      setDobError("You must be 18 years or older.");
    } else {
      setDobError("");
    }
    setDob(e.target.value);
  };

  return (
    <Container className="d-flex justify-content-center align-items-center vh-100">
      <Row>
        <Col>
          <div className="p-4 border rounded shadow-sm bg-light">
            <h4 className="text-center mb-4">Employee Registration</h4>
            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3">
                <Form.Label>Employee Code</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter Employee Code"
                  value={employeeCode}
                  onChange={(e) => setEmployeeCode(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Fullname</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter Fullname"
                  value={fullname}
                  onChange={(e) => setFullname(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Mobile</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter Mobile Number"
                  value={mobile}
                  onChange={(e) => setMobile(e.target.value)}
                  pattern="^\d{10}$"
                  title="Please enter a valid 10-digit mobile number"
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Enter Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  pattern="^[^\s@]+@[^\s@]+\.[^\s@]+$"
                  title="Please enter a valid email address"
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Date of Birth</Form.Label>
                <Form.Control
                  type="date"
                  value={dob}
                  onChange={(e) => setDob(e.target.value)}
                  required
                />
                {!isAgeValid() && (
                  <Form.Text className="text-danger">
                    You must be at least 18 years old.
                  </Form.Text>
                )}
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Profile Image</Form.Label>
                <InputGroup>
                  <Form.Control type="file" onChange={handleImageUpload} />
                  <InputGroup.Text>
                    <CloudUpload />
                  </InputGroup.Text>
                </InputGroup>
              </Form.Group>

              <div className="d-flex justify-content-between">
                <Button variant="secondary">
                  <ArrowLeft className="me-2" />
                  Back
                </Button>
                <Button variant="primary" type="submit" disabled={!isAgeValid()}>
                  <Save className="me-2" />
                  Save
                </Button>
              </div>
            </Form>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default RegistrationForm;
